-></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"verticalAlignment\":\"center\",\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide are-vertically-aligned-center\"><!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"100%\",\"className\":\"is-style-section-1\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center is-style-section-1\" style=\"flex-basis:100%\"><!-- wp:group {\"align\":\"wide\",\"className\":\"is-style-default\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\"},\"blockGap\":\"var:preset|spacing|50\"}},\"layout\":{\"type\":\"constrained\",\"justifyContent\":\"left\"}} -->\n<div class=\"wp-block-group alignwide is-style-default\" style=\"padding-top:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50)\"><!-- wp:image {\"id\":54,\"width\":\"487px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"right\"} -->\n<figure class=\"wp-block-image alignright size-full is-resized\"><img src=\"http://project/wp-content/uploads/2025/03/original-1l0b-2.jpg\" alt=\"\" class=\"wp-image-54\" style=\"width:487px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:columns {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-columns are-vertically-aligned-center\"><!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"100%\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\" style=\"flex-basis:100%\"><!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|50\"},\"layout\":{\"columnSpan\":1,\"rowSpan\":1}},\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"verticalAlignment\":\"space-between\",\"justifyContent\":\"stretch\",\"flexWrap\":\"nowrap\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"className\":\"